/* check out script */git clone https://github.com/CreLab/unraid_pr4100_fan_control.git/* copy all files to save space */cd unraid_pr4100_fan_controlchmod +x fan_control.shmkdir /boot/config/plugins/PR4100_Ctrl/cp ./fan_control.sh /boot/config/plugins/PR4100_Ctrl//* modify go script */echo "cp /boot/config/plugins/PR4100_Ctrl/fan_control.sh /usr/adm/scripts/fan_control-x86_64-CreLab.sh" >> /boot/config/goecho "sh fan_control-x86_64-CreLab.sh > /usr/adm/pr4100_log &" >> /boot/config/go